import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:3002'); // Update to your server URL

const CallPage = ({ fromUserId, toUserId, onClose }) => {
    const [loading, setLoading] = useState(true);
    const [callStatus, setCallStatus] = useState('Calling...');

    useEffect(() => {
        const timer = setTimeout(() => {
            setCallStatus('Call timed out');
            setLoading(false);
            onClose(); // Close the popup after timeout
        }, 10000);

        socket.emit('call', { fromUserId, toUserId });

        socket.on('callRejected', () => {
            setLoading(false);
            setCallStatus('Call rejected');
            onClose(); // Close the popup on rejection
        });

        socket.on('callAnswered', () => {
            setLoading(false);
            setCallStatus('Call answered');
        });

        return () => {
            clearTimeout(timer);
            socket.off('callRejected');
            socket.off('callAnswered');
        };
    }, [fromUserId, toUserId, onClose]);

    const handleCancel = () => {
        socket.emit('rejectCall', { fromUserId, toUserId });
        setLoading(false);
        setCallStatus('Call cancelled');
        onClose(); // Close the popup on cancel
    };

    return (
        <div>
            {loading && <div>{callStatus} <button onClick={handleCancel}>Cancel</button></div>}
            {!loading && <div>{callStatus}</div>}
        </div>
    );
};

export default CallPage;
