import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import defaultAvatar from '../assets/user (1).png'; 
import { PhoneIcon, VideoCameraIcon } from '@heroicons/react/solid';
import 'webrtc-adapter';

const socket = io('http://localhost:3001');

const ChatPage = () => {
    const [messages, setMessages] = useState([]);
    const [messageText, setMessageText] = useState('');
    const [reactions, setReactions] = useState({});
    const [hoveredMessageId, setHoveredMessageId] = useState(null);
    const messageEndRef = useRef(null);
    const localVideoRef = useRef(null);
    const remoteVideoRef = useRef(null);
    const navigate = useNavigate();
    const { receiverId } = useParams();

    const userId = localStorage.getItem('userId');
    const storedToUserId = localStorage.getItem('touserId');
    const [localStream, setLocalStream] = useState(null);
    const [remoteStream, setRemoteStream] = useState(null);
    const [peerConnection, setPeerConnection] = useState(null);
    const [isCallActive, setIsCallActive] = useState(false);
    const [incomingCall, setIncomingCall] = useState(null);
    const [notification, setNotification] = useState(null);

    useEffect(() => {
        if (!receiverId) return;

        socket.on('connect', () => {
            console.log('Connected to socket server');
        });

        socket.on('disconnect', () => {
            console.log('Disconnected from socket server');
        });

        socket.on('incomingCall', (data) => {
            const { offer, from } = data;
            setIncomingCall({ offer, from });
            setNotification(`Incoming call from user ${from}`);
            if (Notification.permission === 'granted') {
                new Notification('Incoming Call', {
                    body: `User ${from} is calling you`,
                    icon: defaultAvatar,
                });
            }
        });

        socket.on('callOffer', async (offer) => {
            if (peerConnection) {
                await peerConnection.setRemoteDescription(new RTCSessionDescription(offer));
                const answer = await peerConnection.createAnswer();
                await peerConnection.setLocalDescription(answer);
                socket.emit('callAnswer', { answer, to: receiverId });
                setIsCallActive(true);
            }
        });

        socket.on('callAnswer', async (answer) => {
            if (peerConnection) {
                await peerConnection.setRemoteDescription(new RTCSessionDescription(answer));
            }
        });

        socket.on('iceCandidate', async (candidate) => {
            if (peerConnection) {
                try {
                    await peerConnection.addIceCandidate(new RTCIceCandidate(candidate));
                } catch (error) {
                    console.error('Error adding ICE candidate:', error);
                }
            }
        });

        fetchMessages();

        return () => {
            socket.off('connect');
            socket.off('disconnect');
            socket.off('incomingCall');
            socket.off('callOffer');
            socket.off('callAnswer');
            socket.off('iceCandidate');
            if (peerConnection) {
                peerConnection.close();
            }
            if (localStream) {
                localStream.getTracks().forEach(track => track.stop());
            }
        };
    }, [receiverId, peerConnection, localStream]);

    const fetchMessages = async () => {
        const receiverId = localStorage.getItem('userId');
        const senderId = localStorage.getItem('touserId');
        try {
            const response = await fetch(`http://localhost:3001/getMessages?fromUserId=${senderId}&toUserId=${receiverId}`);
            const result = await response.json();
            if (Array.isArray(result.messages)) {
                const sortedMessages = result.messages.sort((a, b) => new Date(a.created_at) - new Date(b.created_at));
                setMessages(sortedMessages);
                fetchReactions(sortedMessages.map(msg => msg.id));
            } else {
                throw new Error('Unexpected response format');
            }
        } catch (error) {
            console.error('Failed to fetch messages:', error);
        }
    };

    const fetchReactions = async (messageIds) => {
        try {
            const response = await fetch('http://localhost:3001/getReactions', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ messageIds }),
            });
            const result = await response.json();
            setReactions(result.reactions);
        } catch (error) {
            console.error('Failed to fetch reactions:', error);
        }
    };

    const setupWebRTC = async () => {
        try {
            const pc = new RTCPeerConnection({
                iceServers: [{ urls: 'stun:stun.l.google.com:19302' }],
            });

            pc.ontrack = (event) => {
                setRemoteStream(event.streams[0]);
                if (remoteVideoRef.current) {
                    remoteVideoRef.current.srcObject = event.streams[0];
                }
            };

            pc.onicecandidate = (event) => {
                if (event.candidate) {
                    socket.emit('iceCandidate', { candidate: event.candidate, to: receiverId });
                }
            };

            const stream = await navigator.mediaDevices.getUserMedia({ audio: true, video: true });
            setLocalStream(stream);
            if (localVideoRef.current) {
                localVideoRef.current.srcObject = stream;
            }
            stream.getTracks().forEach((track) => pc.addTrack(track, stream));

            setPeerConnection(pc);
        } catch (error) {
            console.error('Error setting up WebRTC:', error);
        }
    };

    const handleCallUser = async () => {
        if (!receiverId) {
            console.error('Receiver ID is required');
            return;
        }

        if (!peerConnection) {
            await setupWebRTC();
        }

        try {
            const offer = await peerConnection.createOffer();
            await peerConnection.setLocalDescription(offer);
            socket.emit('callOffer', { offer, to: receiverId });
        } catch (error) {
            console.error('Error creating offer:', error);
        }
    };

    const handleEndCall = () => {
        if (peerConnection) {
            peerConnection.close();
            setPeerConnection(null);
        }
        if (localStream) {
            localStream.getTracks().forEach(track => track.stop());
        }
        setLocalStream(null);
        setRemoteStream(null);
        setIsCallActive(false);
    };

    const handleAcceptCall = async () => {
        if (incomingCall) {
            await setupWebRTC();
            await peerConnection.setRemoteDescription(new RTCSessionDescription(incomingCall.offer));
            const answer = await peerConnection.createAnswer();
            await peerConnection.setLocalDescription(answer);
            socket.emit('callAnswer', { answer, to: incomingCall.from });
            setIsCallActive(true);
            setIncomingCall(null);
        }
    };

    const handleRejectCall = () => {
        setIncomingCall(null);
        setNotification(null);
    };

    const handleSendMessage = async () => {
        if (messageText.trim()) {
            try {
                const response = await fetch('http://localhost:3001/sendMessage', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        senderId: userId,
                        receiverId: storedToUserId,
                        text: messageText,
                    }),
                });
                const result = await response.json();
                if (response.ok) {
                    setMessageText('');
                } else {
                    console.error('SendMessage Error:', result.message);
                }
            } catch (error) {
                console.error('SendMessage Fetch Error:', error);
            }
        }
    };

    const handleDeleteMessage = async (messageId) => {
        try {
            const response = await fetch('http://localhost:3001/deleteMessage', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ messageId }),
            });

            const result = await response.json();
            if (response.ok) {
                setMessages((prevMessages) => prevMessages.filter((msg) => msg.id !== messageId));
            } else {
                console.error('DeleteMessage Error:', result.message);
            }
        } catch (error) {
            console.error('DeleteMessage Fetch Error:', error);
        }
    };

    const handleAddReaction = async (messageId, reactionType) => {
        try {
            const response = await fetch('http://localhost:3001/addReaction', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ messageId, userId, reactionType }),
            });

            if (response.ok) {
                const result = await response.json();
                console.log('Reaction added successfully:', result);
            } else {
                const errorText = await response.text();
                console.error('AddReaction Error:', errorText);
            }
        } catch (error) {
            console.error('AddReaction Fetch Error:', error);
        }
    };

    const isMessageDeletable = (createdAt) => {
        const messageTime = new Date(createdAt);
        const currentTime = new Date();
        const diffMinutes = (currentTime - messageTime) / (1000 * 60);
        return diffMinutes <= 2;
    };

    const scrollToBottom = () => {
        messageEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    return (
        <div className="flex flex-col h-screen">
            <div className="flex-1 overflow-y-auto p-4 bg-gray-100">
                {notification && <div className="p-2 bg-yellow-300 text-black mb-2">{notification}</div>}
                <div className="flex flex-col space-y-4">
                    {messages.map((msg) => (
                        <div
                            key={msg.id}
                            className={`flex ${msg.from_user_id === userId ? 'justify-end' : 'justify-start'}`}
                            onMouseEnter={() => setHoveredMessageId(msg.id)}
                            onMouseLeave={() => setHoveredMessageId(null)}
                        >
                            <div className={`p-2 rounded-lg ${msg.from_user_id === userId ? 'bg-blue-500 text-white' : 'bg-gray-300 text-black'}`}>
                                {msg.content}
                                {hoveredMessageId === msg.id && isMessageDeletable(msg.created_at) && (
                                    <button
                                        className="ml-2 text-red-500"
                                        onClick={() => handleDeleteMessage(msg.id)}
                                    >
                                        Delete
                                    </button>
                                )}
                                <div className="flex space-x-1 mt-1">
                                    {Object.keys(reactions[msg.id] || {}).map((reaction) => (
                                        <span key={reaction}>{reaction} {reactions[msg.id][reaction]}</span>
                                    ))}
                                </div>
                            </div>
                        </div>
                    ))}
                    <div ref={messageEndRef} />
                </div>
            </div>
            <div className="bg-white p-4 border-t border-gray-300 flex items-center space-x-4">
                <textarea
                    className="flex-1 border border-gray-300 p-2 rounded"
                    value={messageText}
                    onChange={(e) => setMessageText(e.target.value)}
                    placeholder="Type a message..."
                />
                <button
                    className="bg-blue-500 text-white p-2 rounded"
                    onClick={handleSendMessage}
                >
                    Send
                </button>
                <button
                    className="bg-green-500 text-white p-2 rounded"
                    onClick={handleCallUser}
                >
                    <PhoneIcon className="w-5 h-5" />
                </button>
                <button
                    className="bg-red-500 text-white p-2 rounded"
                    onClick={handleCallUser}
                >
                    <VideoCameraIcon className="w-5 h-5" />
                </button>
            </div>
            <div>
                {isCallActive && (
                    <div className="flex">
                        <video ref={localVideoRef} autoPlay muted className="w-1/2" />
                        <video ref={remoteVideoRef} autoPlay className="w-1/2" />
                        <button
                            className="bg-red-500 text-white p-2 rounded mt-4"
                            onClick={handleEndCall}
                        >
                            End Call
                        </button>
                    </div>
                )}
                {incomingCall && (
                    <div className="p-4 bg-blue-300 text-white">
                        <button onClick={handleAcceptCall} className="bg-green-500 p-2 rounded mr-2">Accept</button>
                        <button onClick={handleRejectCall} className="bg-red-500 p-2 rounded">Reject</button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default ChatPage;
