import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import defaultAvatar from '../assets/user (1).png'; // Adjust path as necessary
import { PhoneIcon, VideoCameraIcon } from '@heroicons/react/solid';
import 'webrtc-adapter';

const socket = io('http://localhost:3001'); // Replace with your server URL

const ChatPage = () => {
    const [messages, setMessages] = useState([]);
    const [messageText, setMessageText] = useState('');
    const [reactions, setReactions] = useState({});
    const [hoveredMessageId, setHoveredMessageId] = useState(null);
    const messageEndRef = useRef(null);
    const navigate = useNavigate();
    const { receiverId } = useParams();

    const userId = localStorage.getItem('userId');
    const storedToUserId = localStorage.getItem('touserId');
    const [localStream, setLocalStream] = useState(null);
    const [remoteStream, setRemoteStream] = useState(null);
    const [peerConnection, setPeerConnection] = useState(null);

    useEffect(() => {
        if (!receiverId) return;

        socket.on('connect', () => {
            console.log('Connected to socket server');
        });

        socket.on('disconnect', () => {
            console.log('Disconnected from socket server');
        });

        const fetchMessages = async () => {
            const receiverId = localStorage.getItem('userId');
            const senderId = localStorage.getItem('touserId');
            try {
                const response = await fetch(`http://localhost:3001/getMessages?fromUserId=${senderId}&toUserId=${receiverId}`);
                const result = await response.json();
                if (Array.isArray(result.messages)) {
                    setMessages(result.messages);
                    fetchReactions(result.messages.map(msg => msg.id)); // Fetch reactions for these messages
                } else {
                    throw new Error('Unexpected response format');
                }
            } catch (error) {
                console.error('Failed to fetch messages:', error);
            }
        };

        const fetchReactions = async (messageIds) => {
            try {
                const response = await fetch('http://localhost:3001/getReactions', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ messageIds }),
                });
                const result = await response.json();
                setReactions(result.reactions);
            } catch (error) {
                console.error('Failed to fetch reactions:', error);
            }
        };

        fetchMessages();

        socket.emit('joinRoom', receiverId);

        socket.on('message', (message) => {
            console.log('Received message:', message);
            setMessages((prevMessages) => [...prevMessages, message]);
            messageEndRef.current?.scrollIntoView({ behavior: 'smooth' });
        });

        socket.on('messageDeleted', (messageId) => {
            setMessages((prevMessages) => prevMessages.filter((msg) => msg.id !== messageId));
        });

        socket.on('reaction', (reaction) => {
            setReactions((prevReactions) => {
                const updatedReactions = { ...prevReactions };
                if (!updatedReactions[reaction.messageId]) {
                    updatedReactions[reaction.messageId] = [];
                }
                updatedReactions[reaction.messageId] = updatedReactions[reaction.messageId].filter(
                    (r) => r.userId !== reaction.userId
                );
                updatedReactions[reaction.messageId].push(reaction);
                return updatedReactions;
            });
        });

        // WebRTC setup
        const setupWebRTC = async () => {
            const pc = new RTCPeerConnection();
            setPeerConnection(pc);

            // Handle incoming remote stream
            pc.ontrack = (event) => {
                setRemoteStream(event.streams[0]);
            };

            // Handle ICE candidates
            pc.onicecandidate = (event) => {
                if (event.candidate) {
                    socket.emit('iceCandidate', { candidate: event.candidate, to: receiverId });
                }
            };

            // Get local media
            try {
                const stream = await navigator.mediaDevices.getUserMedia({ audio: true, video: true });
                setLocalStream(stream);
                stream.getTracks().forEach((track) => pc.addTrack(track, stream));
            } catch (error) {
                console.error('Error getting local media:', error);
            }

            // Handle incoming offer
            socket.on('callOffer', async (offer) => {
                await pc.setRemoteDescription(new RTCSessionDescription(offer));
                const answer = await pc.createAnswer();
                await pc.setLocalDescription(answer);
                socket.emit('callAnswer', { answer, to: receiverId });
            });

            // Handle incoming answer
            socket.on('callAnswer', async (answer) => {
                await pc.setRemoteDescription(new RTCSessionDescription(answer));
            });

            // Handle incoming ICE candidates
            socket.on('iceCandidate', async (candidate) => {
                try {
                    await pc.addIceCandidate(new RTCIceCandidate(candidate));
                } catch (error) {
                    console.error('Error adding ICE candidate:', error);
                }
            });
        };

        setupWebRTC();

        return () => {
            socket.off('connect');
            socket.off('disconnect');
            socket.off('message');
            socket.off('messageDeleted');
            socket.off('reaction');
            socket.off('callOffer');
            socket.off('callAnswer');
            socket.off('iceCandidate');
            if (peerConnection) {
                peerConnection.close();
            }
            if (localStream) {
                localStream.getTracks().forEach(track => track.stop());
            }
        };
    }, [receiverId, userId]);

    const handleSendMessage = async () => {
        if (messageText.trim()) {
            try {
                const response = await fetch('http://localhost:3001/sendMessage', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        senderId: userId,
                        receiverId: storedToUserId,
                        text: messageText,
                    }),
                });
                const result = await response.json();
                if (response.ok) {
                    setMessageText(''); // Clear the input box
                } else {
                    console.error('SendMessage Error:', result.message);
                }
            } catch (error) {
                console.error('SendMessage Fetch Error:', error);
            }
        }
    };

    const handleDeleteMessage = async (messageId) => {
        try {
            const response = await fetch('http://localhost:3001/deleteMessage', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ messageId }),
            });
            const result = await response.json();
            if (response.ok) {
                // Optionally, you can provide user feedback here
            } else {
                console.error('DeleteMessage Error:', result.message);
            }
        } catch (error) {
            console.error('DeleteMessage Fetch Error:', error);
        }
    };

    const handleAddReaction = async (messageId, reactionType) => {
        try {
            const response = await fetch('http://localhost:3001/addReaction', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ messageId, userId, reactionType }),
            });

            if (response.ok) {
                const result = await response.json();
                console.log('Reaction added successfully:', result);
            } else {
                const errorText = await response.text();
                console.error('AddReaction Error:', errorText);
            }
        } catch (error) {
            console.error('AddReaction Fetch Error:', error);
        }
    };

    const isMessageDeletable = (createdAt) => {
        const messageTime = new Date(createdAt);
        const currentTime = new Date();
        const diffMinutes = (currentTime - messageTime) / (1000 * 60); // Difference in minutes
        return diffMinutes <= 2;
    };

    const handleBack = () => {
        navigate('/home');
    };

    const handleCallUser = (callType) => {
        console.log('Calling user:', receiverId, 'Type:', callType);

        if (peerConnection) {
            peerConnection.createOffer().then(offer => {
                peerConnection.setLocalDescription(offer).then(() => {
                    socket.emit('callOffer', { offer, to: receiverId });
                });
            }).catch(error => {
                console.error('Error creating offer:', error);
            });
        }
    };

    return (
        <div className="min-h-screen flex flex-col bg-gradient-to-r from-slate-300 to-slate-500 p-4">
            <div className="flex items-center mb-4">
                <img
                    src={defaultAvatar}
                    alt="Avatar"
                    className="w-12 h-12 rounded-full mr-4 border-2 border-gray-300"
                />
                <h1 className="text-2xl font-bold">Chat</h1>
            </div>

            <button
                onClick={handleBack}
                type="button"
                className="bg-white text-center w-48 rounded-2xl shadow-md py-2"
            >
                Back to Home
            </button>

            <div className="flex-grow overflow-y-auto">
                <div className="space-y-4">
                    {messages.map((msg) => (
                        <div
                            key={msg.id}
                            className={`flex ${
                                msg.from_user_id === userId ? 'justify-end' : 'justify-start'
                            }`}
                        >
                            <div className={`p-2 rounded-lg ${msg.from_user_id === userId ? 'bg-blue-500 text-white' : 'bg-white text-black'}`}>
                                {msg.content}
                                {isMessageDeletable(msg.created_at) && (
                                    <button
                                        onClick={() => handleDeleteMessage(msg.id)}
                                        className="text-red-500 ml-2"
                                    >
                                        Delete
                                    </button>
                                )}
                                <div
                                    onMouseEnter={() => setHoveredMessageId(msg.id)}
                                    onMouseLeave={() => setHoveredMessageId(null)}
                                >
                                    <div className="flex space-x-2">
                                        {hoveredMessageId === msg.id && (
                                            <>
                                                <button
                                                    onClick={() => handleAddReaction(msg.id, 'like')}
                                                    className="text-blue-500"
                                                >
                                                    Like
                                                </button>
                                                <button
                                                    onClick={() => handleAddReaction(msg.id, 'dislike')}
                                                    className="text-red-500"
                                                >
                                                    Dislike
                                                </button>
                                            </>
                                        )}
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                    <div ref={messageEndRef} />
                </div>
            </div>

            <div className="flex items-center space-x-4 mt-4">
                <button
                    onClick={() => handleCallUser('voice')}
                    className="bg-green-500 text-white p-2 rounded-full"
                >
                    <PhoneIcon className="h-6 w-6" />
                </button>
                <button
                    onClick={() => handleCallUser('video')}
                    className="bg-blue-500 text-white p-2 rounded-full"
                >
                    <VideoCameraIcon className="h-6 w-6" />
                </button>
                <input
                    type="text"
                    value={messageText}
                    onChange={(e) => setMessageText(e.target.value)}
                    className="flex-grow p-2 border rounded-lg"
                />
                <button
                    onClick={handleSendMessage}
                    className="bg-blue-500 text-white p-2 rounded-lg"
                >
                    Send
                </button>
            </div>

            {localStream && (
                <div className="fixed bottom-4 right-4 bg-white p-2 rounded-lg shadow-lg">
                    <h2 className="text-lg font-bold">Local Stream</h2>
                    <video
                        autoPlay
                        playsInline
                        muted
                        ref={(video) => {
                            if (video) {
                                video.srcObject = localStream;
                            }
                        }}
                        className="w-32 h-32"
                    />
                </div>
            )}

            {remoteStream && (
                <div className="fixed bottom-4 left-4 bg-white p-2 rounded-lg shadow-lg">
                    <h2 className="text-lg font-bold">Remote Stream</h2>
                    <video
                        autoPlay
                        playsInline
                        ref={(video) => {
                            if (video) {
                                video.srcObject = remoteStream;
                            }
                        }}
                        className="w-32 h-32"
                    />
                </div>
            )}
        </div>
    );
};

export default ChatPage;
