  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();
  const userId = localStorage.getItem('userId');
  useEffect(() => {
    const fetchUsers = async () => {
      if (!userId) return;
      try {
        const response = await axios.get('http://localhost:3000/users', {
          params: {
            userId
          }
        });
        if (response.data && Array.isArray(response.data)) {
          setUserList(response.data);
        } else {
          console.error('Unexpected response format:', response.data);
          setError('Unexpected response format');
        }
      } catch (err) {
        console.error('Error fetching users:', err);
        setError('Error fetching users');
      }
    };
    fetchUsers();
  }, [userId]);
  useEffect(() => {
    const fetchMessagedUsers = async () => {
      if (!userId) return;
      try {
        const response = await axios.get('http://localhost:3000/messagedUsers', {
          params: {
            userId
          }
        });
        if (response.data && Array.isArray(response.data)) {
          setMessagedUserList(response.data);
        } else {
          console.error('Unexpected response format:', response.data);
          setError('Unexpected response format');
        }
      } catch (err) {
        console.error('Error fetching messaged users:', err);
        setError('Error fetching messaged users');
      }
    };
    fetchMessagedUsers();
  }, [userId]);
  const handleLogout = () => {
    localStorage.removeItem('userId');
    localStorage.removeItem('userEmail');
    navigate('/');
  };
  const debouncedSearch = debounce(async term => {
    if (term.trim()) {
      const url = `http://localhost:3000/users?search=${encodeURIComponent(term)}`;
      console.log('Search URL:', url);
      try {
        const response = await axios.get(url);
        setUserList(response.data);
      } catch (err) {
        console.error('Error searching users:', err);
        setError('Failed to search users.');
      }
    } else {
      setUserList([]);
    }
  }, 300);
  useEffect(() => {
    debouncedSearch(searchTerm);
  }, [searchTerm]);
  const handleMessageUser = async user => {
    try {
      navigate(`/chat/${user.username}`);
      localStorage.setItem('touserId', user.id);
      localStorage.setItem('tousername', user.username);
      localStorage.setItem('touserEmail', user.email);
    } catch (err) {
      console.error('Error starting conversation:', err);
      setError('Failed to start conversation.');
