// export default AudioCall;


import React, { useState, useRef, useEffect } from 'react';
import io from 'socket.io-client';
import SimplePeer from 'simple-peer';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const socket = io('http://localhost:3000', {
  transports: ['websocket'],
  withCredentials: true
});

const AudioCall = ({ receiverId }) => {
  const [stream, setStream] = useState(null);
  const [peer, setPeer] = useState(null);
  const [calling, setCalling] = useState(false);
  const localAudioRef = useRef(null);
  const remoteAudioRef = useRef(null);

  useEffect(() => {
    const initMediaStream = async () => {
      try {
        const mediaStream = await navigator.mediaDevices.getUserMedia({ audio: true });
        setStream(mediaStream);
        if (localAudioRef.current) {
          localAudioRef.current.srcObject = mediaStream;
        }
      } catch (error) {
        console.error('Error accessing media devices:', error);
      }
    };

    initMediaStream();

    if ('Notification' in window) {
      Notification.requestPermission().then(permission => {
        if (permission !== 'granted') {
          console.log('Notification permission denied');
        }
      });
    }

    socket.on('offer', handleOffer);
    socket.on('answer', handleAnswer);
    socket.on('ice-candidate', handleIceCandidate);

    return () => {
      socket.off('offer', handleOffer);
      socket.off('answer', handleAnswer);
      socket.off('ice-candidate', handleIceCandidate);
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
      if (peer) {
        peer.destroy();
      }
    };
  }, [stream, peer]);

  const startCall = () => {
    const newPeer = new SimplePeer({
      initiator: true,
      trickle: false,
      stream: stream,
    });

    newPeer.on('signal', (data) => {
      socket.emit('offer', { offer: data, to: receiverId });
    });

    newPeer.on('stream', (remoteStream) => {
      if (remoteAudioRef.current) {
        remoteAudioRef.current.srcObject = remoteStream;
      }
    });

    newPeer.on('error', (err) => console.error('Peer error:', err));

    setPeer(newPeer);
    setCalling(true);
  };

  const handleOffer = (data) => {
    if (Notification.permission === 'granted') {
      new Notification('Incoming Call', {
        body: 'You have an incoming audio call from a user.',
        icon: '/path/to/icon.png'
      });
    }

    toast('Incoming call from user');

    const newPeer = new SimplePeer({
      initiator: false,
      trickle: false,
    });

    newPeer.on('signal', (signal) => {
      socket.emit('answer', { answer: signal, to: receiverId });
    });

    newPeer.on('stream', (remoteStream) => {
      if (remoteAudioRef.current) {
        remoteAudioRef.current.srcObject = remoteStream;
      }
    });

    newPeer.on('error', (err) => console.error('Peer error:', err));

    newPeer.signal(data.offer);
    setPeer(newPeer);
    setCalling(true);
  };

  const handleAnswer = (data) => {
    if (peer) {
      peer.signal(data.answer);
    } else {
      console.error('Peer connection is not initialized.');
    }
  };

  const handleIceCandidate = (data) => {
    if (peer) {
      peer.signal(data.candidate);
    } else {
      console.error('Peer connection is not initialized.');
    }
  };

  const endCall = () => {
    if (peer) {
      peer.destroy();
      setPeer(null);
    }
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }
    setCalling(false);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-2xl mb-4">Audio Call</h1>
      <div className="mb-4">
        <audio ref={localAudioRef} autoPlay muted />
        <audio ref={remoteAudioRef} autoPlay />
      </div>
      {calling ? (
        <button onClick={endCall} className="bg-red-500 text-white p-2 rounded mb-4">
          End Call
        </button>
      ) : (
        <button onClick={startCall} className="bg-blue-500 text-white p-2 rounded mb-4">
          Start Call
        </button>
      )}
      <div className="w-32 aspect-square rounded-full relative flex justify-center items-center animate-spin-slow bg-[conic-gradient(white_0deg,white_300deg,transparent_270deg,transparent_360deg)]">
        <span className="absolute w-[85%] aspect-square rounded-full bg-[conic-gradient(#34d399_0deg,#34d399_180deg,transparent_180deg,transparent_360deg)]" />
      </div>
    </div>
  );
};

export default AudioCall;
