import React, { useEffect, useRef, useState } from 'react';
import Peer from 'peerjs';

const VideoCall = () => {
  const [peerId, setPeerId] = useState('');
  const [remotePeerIdValue, setRemotePeerIdValue] = useState('');
  const remoteVideoRef = useRef(null);
  const currentUserVideoRef = useRef(null);
  const peerInstance = useRef(null);

  useEffect(() => {
    const peer = new Peer();

    peer.on('open', (id) => {
      setPeerId(id);
    });

    peer.on('call', (call) => {
      // Get user media for the current user
      navigator.mediaDevices.getUserMedia({ video: true, audio: true })
        .then((mediaStream) => {
          // Display the current user's video feed
          currentUserVideoRef.current.srcObject = mediaStream;
          currentUserVideoRef.current.play().catch(console.error);

          // Answer the call with the current user's media stream
          call.answer(mediaStream);
          
          // When the remote stream is received
          call.on('stream', (remoteStream) => {
            // Display the remote user's video feed
            remoteVideoRef.current.srcObject = remoteStream;
            remoteVideoRef.current.play().catch(console.error);
          });
        });
    });

    peerInstance.current = peer;

    return () => {
      // Cleanup on component unmount
      peer.destroy();
      const currentStream = currentUserVideoRef.current.srcObject;
      if (currentStream) {
        const tracks = currentStream.getTracks();
        tracks.forEach(track => track.stop());
      }
      const remoteStreamElement = remoteVideoRef.current.srcObject;
      if (remoteStreamElement) {
        const tracks = remoteStreamElement.getTracks();
        tracks.forEach(track => track.stop());
      }
    };
  }, []);

  const call = (remotePeerId) => {
    // Get user media for the current user
    navigator.mediaDevices.getUserMedia({ video: true, audio: true })
      .then((mediaStream) => {
        // Display the current user's video feed
        currentUserVideoRef.current.srcObject = mediaStream;
        currentUserVideoRef.current.play().catch(console.error);

        // Initiate a call to the remote peer with the current user's media stream
        const call = peerInstance.current.call(remotePeerId, mediaStream);

        // When the remote stream is received
        call.on('stream', (remoteStream) => {
          // Display the remote user's video feed
          remoteVideoRef.current.srcObject = remoteStream;
          remoteVideoRef.current.play().catch(console.error);
        });
      });
  };

  return (
    <div className="App">
      <h1>Current user id is {peerId}</h1>
      <input
        type="text"
        value={remotePeerIdValue}
        onChange={e => setRemotePeerIdValue(e.target.value)}
        placeholder="Enter remote peer ID"
      />
      <button onClick={() => call(remotePeerIdValue)}>Call</button>
      <div>
        <video ref={currentUserVideoRef} autoPlay muted />
      </div>
      <div>
        <video ref={remoteVideoRef} autoPlay />
      </div>
    </div>
  );
};

export default VideoCall;
