import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import Peer from 'peerjs';
import defaultAvatar from '../assets/user (1).png'; // Adjust path as necessary
import { PhoneIcon, VideoCameraIcon } from '@heroicons/react/solid';
import 'webrtc-adapter';

// Initialize socket
const socket = io('http://localhost:3001');

const ChatPage = () => {
    const [messages, setMessages] = useState([]);
    const [messageText, setMessageText] = useState('');
    const [reactions, setReactions] = useState({});
    const [hoveredMessageId, setHoveredMessageId] = useState(null);
    const [peer, setPeer] = useState(null);
    const [call, setCall] = useState(null);
    const messageEndRef = useRef(null);
    const navigate = useNavigate();
    const { receiverId } = useParams();
    const userId = localStorage.getItem('userId');
    const storedToUserId = localStorage.getItem('touserId');

    useEffect(() => {
        if (!receiverId) return;

        socket.on('connect', () => {
            console.log('Connected to socket server');
        });

        socket.on('disconnect', () => {
            console.log('Disconnected from socket server');
        });

        const fetchMessages = async () => {
            try {
                const response = await fetch(`http://localhost:3001/getMessages?fromUserId=${storedToUserId}&toUserId=${receiverId}`);
                const result = await response.json();
                if (Array.isArray(result.messages)) {
                    setMessages(result.messages);
                    fetchReactions(result.messages.map(msg => msg.id));
                } else {
                    throw new Error('Unexpected response format');
                }
            } catch (error) {
                console.error('Failed to fetch messages:', error);
            }
        };

        const fetchReactions = async (messageIds) => {
            try {
                const response = await fetch('http://localhost:3001/getReactions', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ messageIds }),
                });
                const result = await response.json();
                setReactions(result.reactions);
            } catch (error) {
                console.error('Failed to fetch reactions:', error);
            }
        };

        fetchMessages();

        socket.emit('joinRoom', receiverId);

        socket.on('message', (message) => {
            console.log('Received message:', message);
            setMessages((prevMessages) => [...prevMessages, message]);
            messageEndRef.current?.scrollIntoView({ behavior: 'smooth' });
        });

        socket.on('messageDeleted', (messageId) => {
            setMessages((prevMessages) => prevMessages.filter((msg) => msg.id !== messageId));
        });

        socket.on('reaction', (reaction) => {
            setReactions((prevReactions) => {
                const updatedReactions = { ...prevReactions };
                if (!updatedReactions[reaction.messageId]) {
                    updatedReactions[reaction.messageId] = [];
                }
                updatedReactions[reaction.messageId] = updatedReactions[reaction.messageId].filter(
                    (r) => r.userId !== reaction.userId
                );
                updatedReactions[reaction.messageId].push(reaction);
                return updatedReactions;
            });
        });

        const peerInstance = new Peer(userId, {
            host: 'localhost',
            port: 9000,
            path: '/peerjs',
        });

        peerInstance.on('open', id => {
            console.log('Peer connected with ID:', id);
            setPeer(peerInstance);
        });

        peerInstance.on('call', call => {
            call.answer(); // Answer the call automatically
            call.on('stream', stream => {
                const video = document.getElementById('remoteVideo');
                if (video) {
                    video.srcObject = stream;
                }
            });
        });

        return () => {
            socket.off('connect');
            socket.off('disconnect');
            socket.off('message');
            socket.off('messageDeleted');
            socket.off('reaction');
        };
    }, [receiverId, userId]);

    const handleSendMessage = async () => {
        if (messageText.trim()) {
            try {
                const response = await fetch('http://localhost:3001/sendMessage', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        senderId: userId,
                        receiverId: storedToUserId,
                        text: messageText,
                    }),
                });
                const result = await response.json();
                if (response.ok) {
                    setMessageText('');
                } else {
                    console.error('SendMessage Error:', result.message);
                }
            } catch (error) {
                console.error('SendMessage Fetch Error:', error);
            }
        }
    };

    const handleDeleteMessage = async (messageId) => {
        try {
            const response = await fetch('http://localhost:3001/deleteMessage', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ messageId }),
            });
            const result = await response.json();
            if (response.ok) {
                // Optionally, you can provide user feedback here
            } else {
                console.error('DeleteMessage Error:', result.message);
            }
        } catch (error) {
            console.error('DeleteMessage Fetch Error:', error);
        }
    };

    const handleAddReaction = async (messageId, reactionType) => {
        try {
            const response = await fetch('http://localhost:3001/addReaction', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ messageId, userId, reactionType }),
            });

            if (response.ok) {
                const result = await response.json();
                console.log('Reaction added successfully:', result);
            } else {
                const errorText = await response.text();
                console.error('AddReaction Error:', errorText);
            }
        } catch (error) {
            console.error('AddReaction Fetch Error:', error);
        }
    };

    const isMessageDeletable = (createdAt) => {
        const messageTime = new Date(createdAt);
        const currentTime = new Date();
        const diffMinutes = (currentTime - messageTime) / (1000 * 60); // Difference in minutes
        return diffMinutes <= 2;
    };

    const handleBack = () => {
        navigate('/home');
    };

    const handleCallUser = (isVideoCall) => {
        if (peer) {
            navigator.mediaDevices.getUserMedia({ audio: true, video: isVideoCall })
                .then(stream => {
                    const call = peer.call(receiverId, stream);
                    setCall(call);
                    call.on('stream', remoteStream => {
                        const video = document.getElementById('remoteVideo');
                        if (video) {
                            video.srcObject = remoteStream;
                        }
                    });
                })
                .catch(error => {
                    console.error('Error accessing media devices:', error);
                });
        }
    };

    return (
        <div className="min-h-screen flex flex-col bg-gradient-to-r from-slate-300 to-slate-500 p-4">
            <div className="flex items-center justify-between mb-4">
                <button onClick={handleBack} className="text-white bg-blue-500 p-2 rounded">
                    Back
                </button>
                <div className="flex space-x-4">
                    <button onClick={() => handleCallUser(false)} className="text-white bg-green-500 p-2 rounded">
                        <PhoneIcon className="h-6 w-6" />
                    </button>
                    <button onClick={() => handleCallUser(true)} className="text-white bg-red-500 p-2 rounded">
                        <VideoCameraIcon className="h-6 w-6" />
                    </button>
                </div>
            </div>
            <div className="flex flex-col flex-grow overflow-hidden">
                <div className="flex-grow overflow-auto">
                    <div className="p-4">
                        {messages.map((message) => (
                            <div
                                key={message.id}
                                className={`flex ${message.from_user_id === userId ? 'justify-end' : 'justify-start'} mb-2`}
                                onMouseEnter={() => setHoveredMessageId(message.id)}
                                onMouseLeave={() => setHoveredMessageId(null)}
                            >
                                <div
                                    className={`p-2 rounded ${message.from_user_id === userId ? 'bg-blue-500 text-white' : 'bg-gray-300'}`}
                                >
                                    {message.content}
                                    {isMessageDeletable(message.created_at) && hoveredMessageId === message.id && (
                                        <button
                                            onClick={() => handleDeleteMessage(message.id)}
                                            className="text-red-500 ml-2"
                                        >
                                            Delete
                                        </button>
                                    )}
                                </div>
                            </div>
                        ))}
                        <div ref={messageEndRef} />
                    </div>
                </div>
                <div className="flex items-center p-4 border-t">
                    <input
                        type="text"
                        value={messageText}
                        onChange={(e) => setMessageText(e.target.value)}
                        className="flex-grow p-2 border rounded"
                        placeholder="Type a message..."
                    />
                    <button
                        onClick={handleSendMessage}
                        className="bg-blue-500 text-white p-2 rounded ml-2"
                    >
                        Send
                    </button>
                </div>
            </div>
            <video id="remoteVideo" autoPlay className="hidden" />
        </div>
    );
};

export default ChatPage;
