import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import Peer from 'peerjs';
import defaultAvatar from '../assets/user (1).png'; // Adjust path as necessary
import { PhoneIcon, VideoCameraIcon } from '@heroicons/react/solid';
import 'webrtc-adapter'; // For WebRTC compatibility

const socket = io('http://localhost:3001'); // Replace with your server URL

const ChatPage = () => {
    const [messages, setMessages] = useState([]);
    const [messageText, setMessageText] = useState('');
    const [reactions, setReactions] = useState({});
    const [hoveredMessageId, setHoveredMessageId] = useState(null);
    const [myStream, setMyStream] = useState(null);
    const [remoteStream, setRemoteStream] = useState(null);
    const [callActive, setCallActive] = useState(false);
    const [currentCall, setCurrentCall] = useState(null);
    const messageEndRef = useRef(null);
    const navigate = useNavigate();
    const { receiverId } = useParams();
    const userId = localStorage.getItem('userId');
    const storedToUserId = localStorage.getItem('touserId');

    // Initialize PeerJS
    const peer = new Peer(userId, { host: 'localhost', port: 3001, path: '/peerjs' });

    useEffect(() => {
        if (!receiverId) return;

        // Setup socket connection
        socket.on('connect', () => {
            console.log('Connected to socket server');
        });

        socket.on('disconnect', () => {
            console.log('Disconnected from socket server');
        });

        const fetchMessages = async () => {
            try {
                const response = await fetch(`http://localhost:3001/getMessages?fromUserId=${storedToUserId}&toUserId=${userId}`);
                const result = await response.json();
                if (Array.isArray(result.messages)) {
                    setMessages(result.messages);
                    fetchReactions(result.messages.map(msg => msg.id)); // Fetch reactions for these messages
                } else {
                    throw new Error('Unexpected response format');
                }
            } catch (error) {
                console.error('Failed to fetch messages:', error);
            }
        };

        const fetchReactions = async (messageIds) => {
            try {
                const response = await fetch('http://localhost:3001/getReactions', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ messageIds }),
                });
                const result = await response.json();
                setReactions(result.reactions);
            } catch (error) {
                console.error('Failed to fetch reactions:', error);
            }
        };

        fetchMessages();

        socket.emit('joinRoom', receiverId);

        socket.on('message', (message) => {
            console.log('Received message:', message);
            setMessages((prevMessages) => [...prevMessages, message]);
            messageEndRef.current?.scrollIntoView({ behavior: 'smooth' });
        });

        socket.on('messageDeleted', (messageId) => {
            setMessages((prevMessages) => prevMessages.filter((msg) => msg.id !== messageId));
        });

        socket.on('reaction', (reaction) => {
            setReactions((prevReactions) => {
                const updatedReactions = { ...prevReactions };
                if (!updatedReactions[reaction.messageId]) {
                    updatedReactions[reaction.messageId] = [];
                }
                updatedReactions[reaction.messageId] = updatedReactions[reaction.messageId].filter(
                    (r) => r.userId !== reaction.userId
                );
                updatedReactions[reaction.messageId].push(reaction);
                return updatedReactions;
            });
        });

        // Setup WebRTC
        const getUserMedia = async () => {
            try {
                const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
                setMyStream(stream);
            } catch (error) {
                console.error('Error accessing media devices.', error);
            }
        };

        getUserMedia();

        peer.on('call', (call) => {
            call.answer(myStream);
            call.on('stream', (stream) => {
                setRemoteStream(stream);
                setCallActive(true);
                setCurrentCall(call);
            });
        });

        return () => {
            socket.off('connect');
            socket.off('disconnect');
            socket.off('message');
            socket.off('messageDeleted');
            socket.off('reaction');
            peer.destroy();
        };
    }, [receiverId, userId, myStream, peer]);

    const handleSendMessage = async () => {
        if (messageText.trim()) {
            try {
                const response = await fetch('http://localhost:3001/sendMessage', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({
                        senderId: userId,
                        receiverId: storedToUserId,
                        text: messageText,
                    }),
                });
                const result = await response.json();
                if (response.ok) {
                    setMessageText(''); // Clear the input box
                } else {
                    console.error('SendMessage Error:', result.message);
                }
            } catch (error) {
                console.error('SendMessage Fetch Error:', error);
            }
        }
    };

    const handleDeleteMessage = async (messageId) => {
        try {
            const response = await fetch('http://localhost:3001/deleteMessage', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ messageId }),
            });
            const result = await response.json();
            if (response.ok) {
                // Optionally, you can provide user feedback here
            } else {
                console.error('DeleteMessage Error:', result.message);
            }
        } catch (error) {
            console.error('DeleteMessage Fetch Error:', error);
        }
    };

    const handleAddReaction = async (messageId, reactionType) => {
        try {
            const response = await fetch('http://localhost:3001/addReaction', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ messageId, userId, reactionType }),
            });
            if (response.ok) {
                const result = await response.json();
                console.log('Reaction added successfully:', result);
            } else {
                const errorText = await response.text();
                console.error('AddReaction Error:', errorText);
            }
        } catch (error) {
            console.error('AddReaction Fetch Error:', error);
        }
    };

    const isMessageDeletable = (createdAt) => {
        const messageTime = new Date(createdAt);
        const currentTime = new Date();
        const diffMinutes = (currentTime - messageTime) / (1000 * 60); // Difference in minutes
        return diffMinutes <= 2;
    };

    const handleBack = () => {
        navigate('/home');
    };

    const handleCallUser = () => {
        if (callActive) {
            handleEndCall();
        } else {
            // Initiate voice call
            const call = peer.call(receiverId, myStream);
            call.on('stream', (stream) => {
                setRemoteStream(stream);
                setCallActive(true);
                setCurrentCall(call);
            });
        }
    };

    const handleEndCall = () => {
        if (currentCall) {
            currentCall.close();
            setCallActive(false);
            setRemoteStream(null);
            setCurrentCall(null);
        }
    };

    return (
        <div className="min-h-screen flex flex-col bg-gradient-to-r from-slate-300 to-slate-500 p-4">
            <div className="flex items-center mb-4">
                <img
                    src={defaultAvatar}
                    alt="Avatar"
                    className="w-12 h-12 rounded-full mr-4 border-2 border-gray-300"
                />
                <h1 className="text-2xl font-bold">Chat</h1>
            </div>

            <button
                onClick={handleBack}
                type="button"
                className="bg-white text-center w-48 rounded-2xl h-14 relative font-sans text-black text-xl font-semibold group justify-end mt-(-4)"
            >
                <div
                    className="bg-green-400 rounded-xl h-12 w-1/4 flex items-center justify-center absolute left-1 top-[4px] group-hover:w-[184px] z-10 duration-500"
                >
                    <svg
                        width="25px"
                        height="25px"
                        viewBox="0 0 1024 1024"
                        xmlns="http://www.w3.org/2000/svg"
                    >
                        <path
                            fill="#000000"
                            d="M224 480h640a32 32 0 1 1 0 64H224a32 32 0 0 1 0-64z"
                        ></path>
                        <path
                            fill="#000000"
                            d="m237.248 512 265.408 265.344a32 32 0 0 0 45.568-45.28L282.4 496h575.168a32 32 0 0 0 0-64H282.4l265.824-265.824a32 32 0 1 0-45.568-45.28L224 448a32 32 0 0 0 0 64z"
                        ></path>
                    </svg>
                </div>
                Back
            </button>

            <div className="flex justify-between items-center mb-2">
                <button
                    onClick={handleCallUser}
                    className="bg-blue-500 text-white p-2 rounded-lg flex items-center"
                >
                    <PhoneIcon className="w-6 h-6 mr-2" />
                    {callActive ? 'End Call' : 'Start Call'}
                </button>
            </div>

            <div className="overflow-auto flex-1 mb-2">
                <div className="flex flex-col-reverse">
                    {messages.map((message) => (
                        <div
                            key={message.id}
                            className={`p-2 my-2 rounded-lg ${
                                message.from_user_id === userId
                                    ? 'bg-blue-500 text-white self-end'
                                    : 'bg-gray-300 text-black self-start'
                            }`}
                            onMouseEnter={() => setHoveredMessageId(message.id)}
                            onMouseLeave={() => setHoveredMessageId(null)}
                        >
                            <p>{message.content}</p>
                            {hoveredMessageId === message.id && isMessageDeletable(message.created_at) && (
                                <button
                                    onClick={() => handleDeleteMessage(message.id)}
                                    className="text-red-500 mt-1"
                                >
                                    Delete
                                </button>
                            )}
                            {reactions[message.id] && reactions[message.id].map((reaction) => (
                                <span key={reaction.userId} className="mr-2">
                                    {reaction.reactionType}
                                </span>
                            ))}
                        </div>
                    ))}
                    <div ref={messageEndRef} />
                </div>
            </div>

            <div className="flex items-center">
                <input
                    type="text"
                    value={messageText}
                    onChange={(e) => setMessageText(e.target.value)}
                    className="flex-1 p-2 border rounded-lg mr-2"
                />
                <button
                    onClick={handleSendMessage}
                    className="bg-blue-500 text-white p-2 rounded-lg"
                >
                    Send
                </button>
            </div>

            {callActive && (
                <div className="fixed bottom-0 right-0 p-4 bg-white border border-gray-300 rounded-lg shadow-lg">
                    <h2 className="text-xl font-semibold mb-2">Call in Progress</h2>
                    <div className="relative">
                        <video
                            ref={(video) => {
                                if (video) {
                                    video.srcObject = myStream;
                                    video.play();
                                }
                            }}
                            className="w-32 h-32"
                            muted
                        />
                        <video
                            ref={(video) => {
                                if (video) {
                                    video.srcObject = remoteStream;
                                    video.play();
                                }
                            }}
                            className="absolute top-0 left-0 w-32 h-32"
                        />
                    </div>
                    <button
                        onClick={handleEndCall}
                        className="mt-4 bg-red-500 text-white py-2 px-4 rounded"
                    >
                        End Call
                    </button>
                </div>
            )}
        </div>
    );
};

export default ChatPage;
