import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import defaultAvatar from '../assets/user (1).png'; // Adjust path as necessary
import { PhoneIcon, VideoCameraIcon } from '@heroicons/react/solid';

const socket = io('http://localhost:3001'); // Replace with your server URL

const ChatPage = () => {
    const [messages, setMessages] = useState([]);
    const [messageText, setMessageText] = useState('');
    const [reactions, setReactions] = useState({});
    const [hoveredMessageId, setHoveredMessageId] = useState(null);
    const messageEndRef = useRef(null);
    const navigate = useNavigate();
    const { receiverId } = useParams();

    const userId = localStorage.getItem('userId');
    const storedToUserId = localStorage.getItem('touserId');

    useEffect(() => {
        if (!receiverId) return;

        console.log('Receiver ID:', receiverId);
        console.log('Stored User ID:', storedToUserId);

        socket.on('connect', () => {
            console.log('Connected to socket server');
        });

        socket.on('disconnect', () => {
            console.log('Disconnected from socket server');
        });

        socket.on('message', (message) => {
            console.log('Received message:', message);
            setMessages((prevMessages) => [...prevMessages, message]);
            messageEndRef.current?.scrollIntoView({ behavior: 'smooth' });
        });

        socket.on('messageDeleted', (messageId) => {
            console.log('Message deleted:', messageId);
            setMessages((prevMessages) => prevMessages.filter((msg) => msg.id !== messageId));
        });

        socket.on('reaction', (reaction) => {
            console.log('Received reaction:', reaction);
            setReactions((prevReactions) => {
                const updatedReactions = { ...prevReactions };
                if (!updatedReactions[reaction.messageId]) {
                    updatedReactions[reaction.messageId] = [];
                }
                updatedReactions[reaction.messageId] = updatedReactions[reaction.messageId].filter(
                    (r) => r.userId !== reaction.userId
                );
                updatedReactions[reaction.messageId].push(reaction);
                return updatedReactions;
            });
        });

        const fetchMessages = async () => {
            try {
                const response = await fetch(`http://localhost:3001/getMessages?fromUserId=${storedToUserId}&toUserId=${receiverId}`);
                const result = await response.json();
                console.log('Fetched messages:', result);
                if (Array.isArray(result.messages)) {
                    setMessages(result.messages);
                    fetchReactions(result.messages.map(msg => msg.id));
                } else {
                    throw new Error('Unexpected response format');
                }
            } catch (error) {
                console.error('Failed to fetch messages:', error);
            }
        };

        const fetchReactions = async (messageIds) => {
            try {
                const response = await fetch('http://localhost:3001/getReactions', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ messageIds }),
                });
                const result = await response.json();
                console.log('Fetched reactions:', result);
                setReactions(result.reactions);
            } catch (error) {
                console.error('Failed to fetch reactions:', error);
            }
        };

        fetchMessages();

        socket.emit('joinRoom', receiverId);

        return () => {
            socket.off('connect');
            socket.off('disconnect');
            socket.off('message');
            socket.off('messageDeleted');
            socket.off('reaction');
        };
    }, [receiverId, userId]);

    const handleSendMessage = async () => {
        if (messageText.trim()) {
            try {
                const response = await fetch('http://localhost:3001/sendMessage', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        senderId: userId,
                        receiverId: storedToUserId,
                        text: messageText,
                    }),
                });
                const result = await response.json();
                console.log('Send message result:', result);
                if (response.ok) {
                    setMessageText('');
                } else {
                    console.error('SendMessage Error:', result.message);
                }
            } catch (error) {
                console.error('SendMessage Fetch Error:', error);
            }
        }
    };

    const handleDeleteMessage = async (messageId) => {
        try {
            const response = await fetch('http://localhost:3001/deleteMessage', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ messageId }),
            });
            const result = await response.json();
            console.log('Delete message result:', result);
            if (response.ok) {
                // Optionally, you can provide user feedback here
            } else {
                console.error('DeleteMessage Error:', result.message);
            }
        } catch (error) {
            console.error('DeleteMessage Fetch Error:', error);
        }
    };

    const handleAddReaction = async (messageId, reactionType) => {
        try {
            const response = await fetch('http://localhost:3001/addReaction', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ messageId, userId, reactionType }),
            });

            if (response.ok) {
                const result = await response.json();
                console.log('Add reaction result:', result);
            } else {
                console.error('AddReaction Error:', await response.text());
            }
        } catch (error) {
            console.error('AddReaction Fetch Error:', error);
        }
    };

    const isMessageDeletable = (createdAt) => {
        const createdAtDate = new Date(createdAt);
        const now = new Date();
        return now - createdAtDate < 2 * 60 * 1000; // 2 minutes in milliseconds
    };

    return (
        <div className="container mx-auto p-4">
            <div className="flex justify-between items-center mb-4">
                <button
                    onClick={() => navigate('/home')}
                    className="bg-gray-300 text-black px-4 py-2 rounded-lg"
                >
                    Back to Home
                </button>
                <div className="flex items-center">
                    <button
                        className="ml-2 p-2 bg-green-500 text-white rounded-lg"
                        onClick={() => console.log('Call clicked')}
                    >
                        <PhoneIcon className="h-6 w-6" />
                    </button>
                    <button
                        className="ml-2 p-2 bg-blue-500 text-white rounded-lg"
                        onClick={() => console.log('Video call clicked')}
                    >
                        <VideoCameraIcon className="h-6 w-6" />
                    </button>
                </div>
            </div>

            <div className="flex">
                <div className="flex-grow border rounded-lg">
                    <div className="p-4 h-96 overflow-auto">
                        <div className="flex flex-col">
                            {messages.map((message) => (
                                <div
                                    key={message.id}
                                    className={`my-2 p-2 rounded-lg ${message.from_user_id === userId ? 'bg-blue-100 self-end' : 'bg-gray-100 self-start'}`}
                                >
                                    <p>{message.content}</p>
                                    {reactions[message.id] && (
                                        <div className="flex space-x-1 mt-2">
                                            {reactions[message.id].map((reaction) => (
                                                <span key={reaction.userId} className="mr-1">{reaction.reactionType}</span>
                                            ))}
                                        </div>
                                    )}
                                    <button
                                        onClick={() => handleDeleteMessage(message.id)}
                                        disabled={!isMessageDeletable(message.created_at)}
                                        className={`mt-1 text-red-500 ${!isMessageDeletable(message.created_at) ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer'}`}
                                    >
                                        Delete
                                    </button>
                                </div>
                            ))}
                            <div ref={messageEndRef} />
                        </div>
                    </div>
                </div>
            </div>

            <div className="flex items-center mt-4">
                <input
                    type="text"
                    value={messageText}
                    onChange={(e) => setMessageText(e.target.value)}
                    placeholder="Type a message"
                    className="flex-grow px-4 py-2 border rounded-lg"
                />
                <button
                    onClick={handleSendMessage}
                    className="ml-4 bg-blue-500 text-white px-4 py-2 rounded-lg"
                >
                    Send
                </button>
            </div>
        </div>
    );
};

export default ChatPage;
