import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import defaultAvatar from '../assets/user (1).png'; // Adjust path as necessary
import { PhoneIcon, VideoCameraIcon, PaperAirplaneIcon } from '@heroicons/react/solid';

const socket = io('http://localhost:3001'); // Replace with your server URL

const ChatPage = () => {
    const [messages, setMessages] = useState([]);
    const [messageText, setMessageText] = useState('');
    const messageEndRef = useRef(null);
    const navigate = useNavigate();
    const { receiverId } = useParams();

    const userId = localStorage.getItem('userId');
    const storedToUserId = localStorage.getItem('touserId');

    useEffect(() => {
        if (!receiverId) return;

        socket.on('connect', () => {
            console.log('Connected to socket server');
        });

        socket.on('disconnect', () => {
            console.log('Disconnected from socket server');
        });

        const fetchMessages = async () => {
            const receiverId = localStorage.getItem('userId');
            const senderId = localStorage.getItem('touserId');
            try {
                const response = await fetch(`http://localhost:3001/getMessages?fromUserId=${senderId}&toUserId=${receiverId}`);
                const result = await response.json();
                console.log("Result:", result);
                if (Array.isArray(result.messages)) {
                    setMessages(result.messages);
                } else {
                    throw new Error('Unexpected response format');
                }
            } catch (error) {
                console.error('Failed to fetch messages:', error);
            }
        };

        fetchMessages();

        socket.emit('joinRoom', receiverId);

        socket.on('message', (message) => {
            console.log('Received message:', message); // Debug message
            setMessages((prevMessages) => [...prevMessages, message]);
            messageEndRef.current?.scrollIntoView({ behavior: 'smooth' });
        });

        socket.on('messageDeleted', (messageId) => {
            setMessages((prevMessages) => prevMessages.filter((msg) => msg.id !== messageId));
        });

        return () => {
            socket.off('connect');
            socket.off('disconnect');
            socket.off('message');
            socket.off('messageDeleted');
        };
    }, [receiverId, userId]);

    const handleSendMessage = async () => {
        if (messageText.trim()) {
            try {
                const response = await fetch('http://localhost:3001/sendMessage', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({
                        senderId: userId,
                        receiverId: storedToUserId,
                        text: messageText,
                    }),
                });
                const result = await response.json();
                if (response.ok) {
                    setMessageText(''); // Clear the input box
                } else {
                    console.error('SendMessage Error:', result.message);
                }
            } catch (error) {
                console.error('SendMessage Fetch Error:', error);
            }
        }
    };

    const handleDeleteMessage = async (messageId) => {
        try {
            await fetch('http://localhost:3001/deleteMessage', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ messageId }),
            });
        } catch (error) {
            console.error('DeleteMessage Error:', error);
        }
    };

    const handleBack = () => {
        navigate('/home');
    };

    const handleCallUser = () => {
        console.log('Calling user:', receiverId);
    };

    return (
        <div className="min-h-screen flex flex-col bg-gradient-to-r from-slate-300 to-slate-500 p-4">
            <div className="flex items-center mb-4">
                <img
                    src={defaultAvatar}
                    alt="Avatar"
                    className="w-12 h-12 rounded-full mr-4 border-2 border-gray-300"
                />
                <h1 className="text-2xl font-bold">Chat</h1>
            </div>

            <button
                onClick={handleBack}
                type="button"
                className="bg-white text-center w-48 rounded-2xl h-14 relative font-sans text-black text-xl font-semibold group justify-end mt-(-4)"
            >
                <div
                    className="bg-green-400 rounded-xl h-12 w-1/4 flex items-center justify-center absolute left-1 top-[4px] group-hover:w-[184px] z-10 duration-500"
                >
                    <svg
                        width="25px"
                        height="25px"
                        viewBox="0 0 1024 1024"
                        xmlns="http://www.w3.org/2000/svg"
                    >
                        <path
                            fill="#000000"
                            d="M224 480h640a32 32 0 1 1 0 64H224a32 32 0 0 1 0-64z"
                        ></path>
                        <path
                            fill="#000000"
                            d="m237.248 512 265.408 265.344a32 32 0 0 1-45.312 45.312l-288-288a32 32 0 0 1 0-45.312l288-288a32 32 0 1 1 45.312 45.312L237.248 512z"
                        ></path>
                    </svg>
                </div>
                <p className="translate-x-2">Go Back</p>
            </button>

            <div className="flex justify-end mb-4">
                <button
                    onClick={handleCallUser}
                    className="bg-green-500 text-white py-2 px-4 rounded mr-2"
                >
                    <PhoneIcon className="w-6 h-6" />
                </button>
                <button
                    onClick={handleCallUser}
                    className="bg-blue-500 text-white py-2 px-4 rounded"
                >
                    <VideoCameraIcon className="w-6 h-6" />
                </button>
            </div>

            <div className="flex flex-col flex-grow">
                {/* Fixed Size Message Container */}
                <div className="w-full max-w-md mx-auto h-69 bg-gray-100 p-4  overflow-y-auto">
                {/* <div className="w-full max-w-xl mx-auto h-96 bg-gray-100 p-4 rounded-xl overflow-y-auto"> */}

                    <div className="flex flex-col gap-2">
                        {messages.length > 0 ? (
                            messages.map((message) => (
                                <div
                                    key={message.id}
                                    className={`p-4 max-w-max rounded shadow ${
                                        message.from_user_id === userId
                                            ? 'bg-blue-200 self-end'
                                            : 'bg-white self-start'
                                    }`}
                                >
                                    <p>{message.content}</p>
                                    {message.from_user_id === userId && (
                                        <button
                                            onClick={() => handleDeleteMessage(message.id)}
                                            className="text-red-500"
                                        >
                                            Delete
                                        </button>
                                    )}
                                </div>
                            ))
                        ) : (
                            <p>No messages yet.</p>
                        )}
                        <div ref={messageEndRef} />
                    </div>
                </div>

                <div className="flex mt-4">
                    <input
                        type="text"
                        value={messageText}
                        onChange={(e) => setMessageText(e.target.value)}
                        placeholder="Type your message..."
                        className="w-full px-3 py-2 border rounded"
                    />
                    <button
                        onClick={handleSendMessage}
                        className="flex items-center justify-center px-4 py-2 bg-blue-500 text-white rounded ml-2"
                    >
                        <PaperAirplaneIcon className="w-6 h-6" />
                    </button>
                </div>
            </div>
        </div>
    );
};

export default ChatPage;
