import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import defaultAvatar from '../assets/user (1).png';
import { PhoneIcon, VideoCameraIcon } from '@heroicons/react/solid';
import { useNavigate } from 'react-router-dom';

const socket = io('http://localhost:3001');

const ChatPage = () => {
    const [messages, setMessages] = useState([]);
    const [messageText, setMessageText] = useState('');
    const messageEndRef = useRef(null);
    const navigate = useNavigate();
    const { receiverId } = useParams(); // Only use receiverId from URL

    useEffect(() => {
        console.log('Receiver ID:', receiverId);

        if (!receiverId) return;

        console.log('Connecting to socket for user:', receiverId);
        socket.emit('joinRoom', receiverId);

        socket.on('message', (message) => {
            console.log('Received message:', message);
            setMessages((prevMessages) => [...prevMessages, message]);
            messageEndRef.current?.scrollIntoView({ behavior: 'smooth' });
        });

        socket.on('messageDeleted', (messageId) => {
            setMessages((prevMessages) => prevMessages.filter((msg) => msg.id !== messageId));
        });

        return () => {
            socket.off('message');
            socket.off('messageDeleted');
            console.log('Socket connection cleaned up');
        };
    }, [receiverId]);

    const handleSendMessage = () => {
        if (messageText.trim()) {
            socket.emit('sendMessage', { sender: receiverId, receiver: receiverId, text: messageText });
            setMessageText('');
        }
    };

    const handleBack = () => {
        navigate('/home');
    };

    const handleCallUser = () => {
        // Logic for video/voice call
        console.log('Calling user:', receiverId);
    };

    const handleDeleteMessage = (messageId) => {
        socket.emit('deleteMessage', { messageId });
    };

    return (
        <div className="min-h-screen flex flex-col bg-gradient-to-r from-slate-300 to-slate-500 p-4">
            <div className="flex items-center mb-4">
                <img
                    src={defaultAvatar}
                    alt="Avatar"
                    className="w-12 h-12 rounded-full mr-4 border-2 border-gray-300"
                />
                {receiverId ? (
                    <h1 className="text-2xl font-bold">{receiverId}</h1>
                ) : (
                    <h1 className="text-2xl font-bold">Loading...</h1>
                )}
            </div>

            {/* Back button */}
            <button
                onClick={handleBack}
                type="button"
                className="bg-white text-center w-48 rounded-2xl h-14 relative font-sans text-black text-xl font-semibold group justify-end mt-(-4)"
            >
                <div
                    className="bg-green-400 rounded-xl h-12 w-1/4 flex items-center justify-center absolute left-1 top-[4px] group-hover:w-[184px] z-10 duration-500"
                >
                    <svg
                        width="25px"
                        height="25px"
                        viewBox="0 0 1024 1024"
                        xmlns="http://www.w3.org/2000/svg"
                    >
                        <path
                            fill="#000000"
                            d="M224 480h640a32 32 0 1 1 0 64H224a32 32 0 0 1 0-64z"
                        ></path>
                        <path
                            fill="#000000"
                            d="m237.248 512 265.408 265.344a32 32 0 0 1-45.312 45.312l-288-288a32 32 0 0 1 0-45.312l288-288a32 32 0 1 1 45.312 45.312L237.248 512z"
                        ></path>
                    </svg>
                </div>
                <p className="translate-x-2">Go Back</p>
            </button>

            {/* Back button end */}

            <div className="flex justify-end mb-4">
                <button
                    onClick={handleCallUser}
                    className="bg-green-500 text-white py-2 px-4 rounded mr-2"
                >
                    <PhoneIcon className="w-6 h-6" />
                </button>
                <button
                    onClick={handleCallUser}
                    className="bg-blue-500 text-white py-2 px-4 rounded"
                >
                    <VideoCameraIcon className="w-6 h-6" />
                </button>
            </div>

            <div className="flex flex-col flex-grow overflow-y-auto mb-4">
                <div className="flex-grow">
                    {messages.map((message) => (
                        <div key={message.id} className="bg-white p-4 mb-2 rounded shadow">
                            <p className="font-semibold">{message.sender_id}</p>
                            <p>{message.content}</p>
                            <button
                                onClick={() => handleDeleteMessage(message.id)}
                                className="text-red-500"
                            >
                                Delete
                            </button>
                        </div>
                    ))}
                    <div ref={messageEndRef} />
                </div>
            </div>

            <div className="flex">
                <input
                    type="text"
                    value={messageText}
                    onChange={(e) => setMessageText(e.target.value)}
                    placeholder="Type your message..."
                    className="w-full px-3 py-2 border rounded"
                />
                <button
                    onClick={handleSendMessage}
                    className="flex items-center bg-blue-500 text-white gap-1 px-4 py-2 cursor-pointer font-semibold tracking-widest rounded-md hover:bg-blue-400 duration-300 hover:gap-2 hover:translate-x-3"
                >
                    Send
                    <svg
                        className="w-5 h-5"
                        stroke="currentColor"
                        strokeWidth="1.5"
                        viewBox="0 0 24 24"
                        fill="none"
                        xmlns="http://www.w3.org/2000/svg"
                    >
                        <path
                            d="M6 12 3.269 3.125A59.769 59.769 0 0 1 21.485 12 59.768 59.768 0 0 1 3.27 20.875L5.999 12Zm0 0h7.5"
                            strokeLinejoin="round"
                            strokeLinecap="round"
                        ></path>
                    </svg>
                </button>
            </div>
        </div>
    );
};

export default ChatPage;
