import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Home = () => {
    const [messages, setMessages] = useState([]);
    const [error, setError] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const [userList, setUserList] = useState([]);
    const [selectedUser, setSelectedUser] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch the list of messages when the component mounts
        const fetchMessages = async () => {
            try {
                const response = await axios.get('http://localhost:3000/messages'); // Adjust the URL as needed
                setMessages(response.data);
            } catch (err) {
                console.error('Error fetching messages:', err);
                setError('Failed to load messages.');
            }
        };

        fetchMessages();
    }, []);

    const handleLogout = () => {
        // Clear user session or token, then redirect to login page
        navigate('/login');
    };

    const handleSearch = async () => {
        if (searchTerm.trim()) {
            try {
                const response = await axios.get(`http://localhost:3000/users?search=${searchTerm}`);
                setUserList(response.data);
            } catch (err) {
                console.error('Error searching users:', err);
                setError('Failed to search users.');
            }
        }
    };

    const handleMessageUser = async (user) => {
        // Logic to start a conversation with the selected user
        try {
            await axios.post('http://localhost:3000/start-conversation', { user });
            navigate(`/chat/${user}`); // Navigate to the chat page with the selected user
        } catch (err) {
            console.error('Error starting conversation:', err);
            setError('Failed to start conversation.');
        }
    };

    return (
        <div className="min-h-screen flex flex-col items-center bg-gradient-to-r from-slate-300 to-slate-500">
            <h1 className="text-3xl font-bold mb-4  pt-6">GoFIBER</h1>
     
            <hr
  class="my-12 h-px border-t-0 bg-transparent bg-gradient-to-r from-transparent via-neutral-500 to-transparent opacity-25 dark:via-neutral-400" />
            <button
                onClick={handleLogout}
                className="bg-red-500 text-white py-2 px-4 rounded mb-4"
            >
                Logout
            </button>
            
            {/* Search bar for users */}
            <div className="mb-4">
                <input
                    type="text"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    placeholder="Search for a user"
                    className="px-3 py-2 border rounded mr-2"
                />
                <button
                    onClick={handleSearch}
                    className="bg-blue-500 text-white py-2 px-4 rounded"
                >
                    Search
                </button>
            </div>

            {error && <p className="text-red-500 mb-4">{error}</p>}

            <ul className="w-full max-w-md mb-4">
                {messages.length === 0 ? (
                    <p>No messages found.</p>
                ) : (
                    messages.map((message) => (
                        <li key={message.id} className="bg-gray-100 p-4 mb-2 rounded shadow">
                            <p className="font-semibold">{message.sender}</p>
                            <p>{message.text}</p>
                        </li>
                    ))
                )}
            </ul>

            {userList.length > 0 && (
                <div className="w-full max-w-md">
                    <h2 className="text-2xl font-bold mb-4">Users</h2>
                    <ul>
                        {userList.map((user) => (
                            <li key={user.id} className="bg-gray-100 p-4 mb-2 rounded shadow">
                                <p className="font-semibold">{user.username}</p>
                                <button
                                    onClick={() => handleMessageUser(user.username)}
                                    className="bg-green-500 text-white py-2 px-4 rounded"
                                >
                                    Message
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Home;
