import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import debounce from 'lodash/debounce';

const Home = () => {
    const [userList, setUserList] = useState([]);
    const [error, setError] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const [popupData, setPopupData] = useState(null);
    const [newUsername, setNewUsername] = useState('');
    const navigate = useNavigate();
    const userId = localStorage.getItem('userId');
    const nm = localStorage.getItem('username');

    useEffect(() => {
        const fetchUsers = async () => {
            if (!userId) return;

            try {
                const response = await axios.get('http://localhost:3000/users', {
                    params: { userId }
                });

                if (response.data && Array.isArray(response.data)) {
                    setUserList(response.data);
                } else {
                    console.error('Unexpected response format:', response.data);
                    setError('Unexpected response format');
                }
            } catch (err) {
                console.error('Error fetching users:', err);
                setError('Error fetching users');
            }
        };

        fetchUsers();
    }, [userId]);

    const handleLogout = () => {
        localStorage.removeItem('userId');
        localStorage.removeItem('userEmail');
        navigate('/');
    };

    const debouncedSearch = debounce(async (term) => {
        if (term.trim()) {
            const url = `http://localhost:3000/users?search=${encodeURIComponent(term)}`;
            console.log('Search URL:', url);
            try {
                const response = await axios.get(url);
                setUserList(response.data);
            } catch (err) {
                console.error('Error searching users:', err);
                setError('Failed to search users.');
            }
        } else {
            setUserList([]);
        }
    }, 300);

    useEffect(() => {
        debouncedSearch(searchTerm);
    }, [searchTerm]);

    const handleMessageUser = async (user) => {
        try {
            navigate(`/chat/${user.username}`);
            localStorage.setItem('touserId', user.id);
            localStorage.setItem('tousername', user.username);
            localStorage.setItem('touserEmail', user.email);
        } catch (err) {
            console.error('Error starting conversation:', err);
            setError('Failed to start conversation.');
        }
    };

    const handleProfile = () => {
        // Open popup with current user data
        setPopupData({
            username: nm,
            email: localStorage.getItem('userEmail')
        });
        setIsPopupOpen(true);
    };

    const handleClosePopup = () => {
        setIsPopupOpen(false);
        setPopupData(null);
        setNewUsername('');
    };

    const handleUpdateUsername = async () => {
        try {
            await axios.put('http://localhost:3000/users/update', {
                userId,
                newUsername
            });
            localStorage.setItem('username', newUsername);
            setPopupData(prev => ({ ...prev, username: newUsername }));
            setNewUsername('');
        } catch (err) {
            console.error('Error updating username:', err);
            setError('Failed to update username.');
        }
    };

    return (
        <div className="min-h-screen flex flex-col items-center bg-gradient-to-r from-slate-300 to-slate-500">
            {/* User Profile */}
            <div className="w-full px-4 flex justify-between items-center mt-4">
                <div className="flex justify-center items-center border-2 border-radius border-white-200 overflow-hidden p-1 rounded-full shadow-lg">
                    <button
                        onClick={handleProfile}
                        className="bg-[linear-gradient(#e9e9e9,#e9e9e9_50%,#fff)] group w-50 h-16 inline-flex transition-all duration-300 overflow-visible p-1 rounded-full group"
                    >
                        <div className="w-full h-full bg-[linear-gradient(to_top,#ececec,#fff)] overflow-hidden shadow-[0_0_1px_rgba(0,0,0,0.07),0_0_1px_rgba(0,0,0,0.05),0_3px_3px_rgba(0,0,0,0.25),0_1px_3px_rgba(0,0,0,0.12)] p-1 rounded-full hover:shadow-none duration-300">
                            <div className="w-full h-full text-xl gap-x-0.5 gap-y-0.5 justify-center text-[#101010] bg-[linear-gradient(#f4f4f4,#fefefe)] group-hover:bg-[linear-gradient(#e2e2e2,#fefefe)] duration-200 items-center text-[18px] font-medium gap-4 inline-flex overflow-hidden px-4 py-2 rounded-full black group-hover:text-blue-600">
                                <span className="ml-2">{nm}</span>
                            </div>
                        </div>
                    </button>
                </div>

                <button
                    onClick={handleLogout}
                    className="group flex items-center justify-start w-11 h-11 bg-red-600 rounded-full cursor-pointer relative overflow-hidden transition-all duration-200 shadow-lg hover:w-32 hover:rounded-lg active:translate-x-1 active:translate-y-1"
                >
                    <div className="flex items-center justify-center w-full transition-all duration-300 group-hover:justify-start group-hover:px-3">
                        <svg className="w-4 h-4" viewBox="0 0 512 512" fill="white">
                            <path d="M377.9 105.9L500.7 228.7c7.2 7.2 11.3 17.1 11.3 27.3s-4.1 20.1-11.3 27.3L377.9 406.1c-6.4 6.4-15 9.9-24 9.9c-18.7 0-33.9-15.2-33.9-33.9l0-62.1-128 0c-17.7 0-32-14.3-32-32l0-64c0-17.7 14.3-32 32-32l128 0 0-62.1c0-18.7 15.2-33.9 33.9-33.9c9 0 17.6 3.6 24 9.9zM160 96L96 96c-17.7 0-32 14.3-32 32l0 256c0 17.7 14.3 32 32 32l64 0c17.7 0 32 14.3 32 32s-14.3 32-32 32l-64 0c-53 0-96-43-96-96L0 128C0 75 43 32 96 32l64 0c17.7 0 32 14.3 32 32s-14.3 32-32 32z"></path>
                        </svg>
                    </div>
                    <div className="absolute right-5 transform translate-x-full opacity-0 text-white text-lg font-semibold transition-all duration-300 group-hover:translate-x-0 group-hover:opacity-100">
                        Logout
                    </div>
                </button>
            </div>

            <h1 className="text-3xl font-bold mb-4 pt-1 bg-gradient-to-r from-emerald-100 to-indigo-200 bg-clip-text text-transparent">GoFIBER</h1>

            <div className="relative flex items-center justify-center mt-4">
                <div className="p-5 overflow-hidden w-[60px] h-[60px] hover:w-[270px] bg-[#7292ec] shadow-[2px_2px_20px_rgba(0,0,0,0.08)] rounded-full flex group items-center hover:duration-300 duration-300">
                    <div className="flex items-center justify-center fill-white">
                        <svg xmlns="http://www.w3.org/2000/svg" id="Isolation_Mode" data-name="Isolation Mode" viewBox="0 0 24 24" width="22" height="22">
                            <path d="M18.9,16.776A10.539,10.539,0,1,0,16.776,18.9l5.1,5.1L24,21.88ZM10.5,18A7.5,7.5,0,1,1,18,10.5,7.507,7.507,0,0,1,10.5,18Z"></path>
                        </svg>
                    </div>
                    <input
                        type="text"
                        className="outline-none text-[20px] bg-transparent w-full text-white font-normal px-4"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        placeholder="Search for a user"
                    />
                </div>
            </div>

            {error && <p className="text-red-500 mb-4">{error}</p>}

            {userList.length > 0 && (
                <div className="w-full max-w-md mt-4">
                    <h2 className="text-2xl font-bold mb-4">Users</h2>
                    <ul>
                        {userList.map((user) => (
                            <li key={user.id} className="bg-gray-100 p-4 mb-2 rounded shadow">
                                <p className="font-semibold">{user.username}</p>
                                <p className="text-sm text-gray-500">{new Date(user.lastMessageDate).toLocaleString()}</p>
                                <button
                                    onClick={() => handleMessageUser(user)}
                                    className="mt-2 bg-green-500 text-white py-2 px-4 rounded"
                                >
                                    Start Conversation
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
            )}

            {isPopupOpen && (
                <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
                    <div className="bg-white p-6 rounded-lg shadow-lg w-96">
                        <h2 className="text-2xl font-bold mb-4">Profile</h2>
                        {popupData && (
                            <div>
                                <p className="mb-2"><strong>Username:</strong> {popupData.username}</p>
                                <p className="mb-4"><strong>Email:</strong> {popupData.email}</p>
                                <input
                                    type="text"
                                    value={newUsername}
                                    onChange={(e) => setNewUsername(e.target.value)}
                                    placeholder="New Username"
                                    className="border p-2 w-full mb-4"
                                />
                                <button
                                    onClick={handleUpdateUsername}
                                    className="bg-blue-500 text-white py-2 px-4 rounded mr-2"
                                >
                                    Update Username
                                </button>
                                <button
                                    onClick={handleClosePopup}
                                    className="bg-gray-500 text-white py-2 px-4 rounded"
                                >
                                    Close
                                </button>
                            </div>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};

export default Home;
