import React, { useRef, useEffect } from 'react';
import { useWebRTC } from '../hooks/WebRTCClient'; // Adjust the path if necessary

const VideoCallPopup = ({ onEndCall }) => {
  const { peerConnection, initiateCall, answerCall } = useWebRTC();
  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);

  useEffect(() => {
    if (!peerConnection) return;

    // Handle local video stream
    const setupLocalStream = async () => {
      try {
        const localStream = await navigator.mediaDevices.getUserMedia({ audio: true, video: true });
        if (localVideoRef.current) {
          localVideoRef.current.srcObject = localStream;
        }
        localStream.getTracks().forEach(track => peerConnection.addTrack(track, localStream));
      } catch (error) {
        console.error('Error accessing media devices.', error);
      }
    };

    // Setup remote video stream
    const handleTrackEvent = (event) => {
      if (remoteVideoRef.current) {
        remoteVideoRef.current.srcObject = event.streams[0];
      }
    };

    peerConnection.ontrack = handleTrackEvent;

    // Cleanup on unmount
    return () => {
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = null;
      }
      if (remoteVideoRef.current) {
        remoteVideoRef.current.srcObject = null;
      }
    };
  }, [peerConnection]);

  useEffect(() => {
    if (!peerConnection) return;

    // Call handling logic
    const handleIncomingOffer = async (offer) => {
      if (peerConnection.signalingState === 'stable') return;
      await peerConnection.setRemoteDescription(new RTCSessionDescription(offer));
      const answer = await peerConnection.createAnswer();
      await peerConnection.setLocalDescription(answer);
      socket.emit('answer', { answer });
    };

    socket.on('offer', handleIncomingOffer);

    return () => {
      socket.off('offer', handleIncomingOffer);
    };
  }, [peerConnection]);

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-60">
      <div className="relative w-full max-w-4xl bg-white rounded-lg shadow-lg">
        <button
          className="absolute top-2 right-2 px-4 py-2 bg-red-500 text-white rounded"
          onClick={onEndCall}
        >
          End Call
        </button>
        <div className="flex">
          <div className="w-1/2 p-2">
            <video
              ref={localVideoRef}
              autoPlay
              muted
              className="w-full h-auto border-2 border-gray-300 rounded"
            />
          </div>
          <div className="w-1/2 p-2">
            <video
              ref={remoteVideoRef}
              autoPlay
              className="w-full h-auto border-2 border-gray-300 rounded"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default VideoCallPopup;
