import React, { useState } from 'react';
import { useWebRTC } from '../hooks/WebRTCClient';
import VideoCallPopup from '../components/VideoCallPopup'; // Import the VideoCallPopup

const VideoCallComponent = () => {
  const { initiateCall, answerCall } = useWebRTC();
  const [showPopup, setShowPopup] = useState(false);

  const handleStartCall = () => {
    initiateCall('receiverId'); // Replace with the actual receiver ID
    setShowPopup(true); // Show the popup when the call is initiated
  };

  const handleEndCall = () => {
    setShowPopup(false); // Hide the popup when the call ends
  };

  return (
    <div>
      <button onClick={handleStartCall}>Start Call</button>
      {/* Show VideoCallPopup conditionally based on state */}
      {showPopup && <VideoCallPopup onEndCall={handleEndCall} />}
    </div>
  );
};

export default VideoCallComponent;
