import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import debounce from 'lodash/debounce'; // Import debounce function

const Home = () => {
    const [messages, setMessages] = useState([]);
    const [error, setError] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const [userList, setUserList] = useState([]);
    const [selectedUser, setSelectedUser] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await axios.get('http://localhost:3000/messages');
                setMessages(response.data);
            } catch (err) {
                console.error('Error fetching messages:', err);
                setError('Failed to load messages.');
            }
        };

        fetchMessages();
    }, []);

    const handleLogout = () => {
        navigate('/');
    };

    const debouncedSearch = debounce(async (term) => {
        if (term.trim()) {
            const url = `http://localhost:3000/users?search=${encodeURIComponent(term)}`;
            console.log('Search URL:', url);
            try {
                const response = await axios.get(url);
                setUserList(response.data);
            } catch (err) {
                console.error('Error searching users:', err);
                setError('Failed to search users.');
            }
        } else {
            setUserList([]);
        }
    }, 300); // Debounce delay in milliseconds

    useEffect(() => {
        debouncedSearch(searchTerm);
    }, [searchTerm]);

    const handleMessageUser = async (username) => {
        try {
            navigate(`/chat/${username}`);
        } catch (err) {
            console.error('Error starting conversation:', err);
            setError('Failed to start conversation.');
        }
    };

    return (


        
        <div className="min-h-screen flex flex-col items-center bg-gradient-to-r from-slate-300 to-slate-500">


<div class="flex bg-white w-fit px-1.25 py-1.25 shadow-box-up rounded-2xl dark:bg-box-dark dark:shadow-box-dark-out">
  <div class="dark:shadow-buttons-box-dark rounded-2xl w-full px-1.5 py-1.5 md:px-3 md:py-3">

    <a title="Go to about me page" class="text-light-blue-light hover:text-black dark:text-gray-400 border-2 inline-flex items-center mr-4 last-of-type:mr-0 p-2.5 border-transparent bg-light-secondary shadow-button-flat-nopressed hover:border-2 hover:shadow-button-flat-pressed focus:opacity-100 focus:outline-none active:border-2 active:shadow-button-flat-pressed font-medium rounded-full text-sm text-center dark:bg-button-curved-default-dark dark:shadow-button-curved-default-dark dark:hover:bg-button-curved-pressed-dark dark:hover:shadow-button-curved-pressed-dark dark:active:bg-button-curved-pressed-dark dark:active:shadow-button-curved-pressed-dark dark:focus:bg-button-curved-pressed-dark dark:focus:shadow-button-curved-pressed-dark dark:border-0">
      <svg xmlns="http://www.w3.org/2000/svg" class="w-5 h-5" viewBox="0 0 20 20" fill="currentColor">
        <path fill-rule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clip-rule="evenodd"></path>
      </svg>
    </a>
  </div>
</div>
            <div className="w-full px-4 flex justify-end mt-4">
                <button
                    onClick={handleLogout}
                    className="group flex items-center justify-start w-11 h-11 bg-red-600 rounded-full cursor-pointer relative overflow-hidden transition-all duration-200 shadow-lg hover:w-32 hover:rounded-lg active:translate-x-1 active:translate-y-1"
                >
                    <div className="flex items-center justify-center w-full transition-all duration-300 group-hover:justify-start group-hover:px-3">
                        <svg className="w-4 h-4" viewBox="0 0 512 512" fill="white">
                            <path d="M377.9 105.9L500.7 228.7c7.2 7.2 11.3 17.1 11.3 27.3s-4.1 20.1-11.3 27.3L377.9 406.1c-6.4 6.4-15 9.9-24 9.9c-18.7 0-33.9-15.2-33.9-33.9l0-62.1-128 0c-17.7 0-32-14.3-32-32l0-64c0-17.7 14.3-32 32-32l128 0 0-62.1c0-18.7 15.2-33.9 33.9-33.9c9 0 17.6 3.6 24 9.9zM160 96L96 96c-17.7 0-32 14.3-32 32l0 256c0 17.7 14.3 32 32 32l64 0c17.7 0 32 14.3 32 32s-14.3 32-32 32l-64 0c-53 0-96-43-96-96L0 128C0 75 43 32 96 32l64 0c17.7 0 32 14.3 32 32s-14.3 32-32 32z"></path>
                        </svg>
                    </div>
                    <div className="absolute right-5 transform translate-x-full opacity-0 text-white text-lg font-semibold transition-all duration-300 group-hover:translate-x-0 group-hover:opacity-100">
                        Logout
                    </div>
                </button>
            </div>
            <h1 className="text-3xl font-bold mb-4 pt-1">GoFIBER</h1>
            
            <div className="relative flex items-center justify-center mt-4">
                <div className="p-5 overflow-hidden w-[60px] h-[60px] hover:w-[270px] bg-[#4070f4] shadow-[2px_2px_20px_rgba(0,0,0,0.08)] rounded-full flex group items-center hover:duration-300 duration-300">
                    <div className="flex items-center justify-center fill-white">
                        <svg xmlns="http://www.w3.org/2000/svg" id="Isolation_Mode" data-name="Isolation Mode" viewBox="0 0 24 24" width="22" height="22">
                            <path d="M18.9,16.776A10.539,10.539,0,1,0,16.776,18.9l5.1,5.1L24,21.88ZM10.5,18A7.5,7.5,0,1,1,18,10.5,7.507,7.507,0,0,1,10.5,18Z"></path>
                        </svg>
                    </div>
                    <input
                        type="text"
                        className="outline-none text-[20px] bg-transparent w-full text-white font-normal px-4"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        placeholder="Search for a user"
                    />
                </div>
            </div>

            {error && <p className="text-red-500 mb-4">{error}</p>}

            {userList.length > 0 && (
                <div className="w-full max-w-md mt-4">
                    <h2 className="text-2xl font-bold mb-4">Users</h2>
                    <ul>
                        {userList.map((user) => (
                            <li key={user.id} className="bg-gray-100 p-4 mb-2 rounded shadow">
                                <p className="font-semibold">{user.username}</p>
                                <button
                                    onClick={() => handleMessageUser(user.username)}
                                    className="bg-green-500 text-white py-2 px-4 rounded"
                                >
                                    Message
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
            )}

            <ul className="w-full max-w-md mb-4">
                {messages.length === 0 ? (
                    <p>No messages found.</p>
                ) : (
                    messages.map((message) => (
                        <li key={message.id} className="bg-gray-100 p-4 mb-2 rounded shadow">
                            <p className="font-semibold">{message.sender}</p>
                            <p>{message.text}</p>
                        </li>
                    ))
                )}
            </ul>
        </div>
    );
};

export default Home;
