import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams from react-router-dom
import { io } from 'socket.io-client';
// You can use a placeholder URL or upload your own avatar images
import defaultAvatar from '../assets/user (1).png';
import { PhoneIcon, VideoCameraIcon } from '@heroicons/react/solid';

const socket = io('http://localhost:3001'); // Adjust the URL if needed

const ChatPage = () => {
    const [messages, setMessages] = useState([]);
    const [messageText, setMessageText] = useState('');
    const messageEndRef = useRef(null);

    const { user } = useParams(); // Use useParams to get the user from URL

    useEffect(() => {
        console.log('Connecting to socket for user:', user);
        socket.emit('joinRoom', user);

        socket.on('message', (message) => {
            console.log('Received message:', message);
            setMessages((prevMessages) => [...prevMessages, message]);
            messageEndRef.current?.scrollIntoView({ behavior: 'smooth' });
        });

        return () => {
            socket.off('message');
            console.log('Socket connection cleaned up');
        };
    }, [user]);

    const handleSendMessage = () => {
        if (messageText.trim()) {
            socket.emit('sendMessage', { user, text: messageText });
            setMessageText('');
        }
    };

    const handleCallUser = () => {
        // Logic for video/voice call
        console.log('Calling user:', user);
    };

    const handleDeleteMessage = (messageId) => {
        socket.emit('deleteMessage', { user, messageId });
    };

    return (
        <div className="min-h-screen flex flex-col bg-gradient-to-r from-slate-300 to-slate-500 p-4">
            <div className="flex items-center mb-4">
                <img
                    src={defaultAvatar} // Replace with user avatar URL if available
                    alt="Avatar"
                    className="w-12 h-12 rounded-full mr-4 border-2 border-gray-300"
                />
                <h1 className="text-2xl font-bold">{user}</h1>
            </div>

            {/* back button */}
            <button
  type="button"
  class="bg-white text-center w-48 rounded-2xl h-14 relative font-sans text-black text-xl font-semibold group justify-end mt-4" >
  <div
    class="bg-green-400 rounded-xl h-12 w-1/4 flex items-center justify-center absolute left-1 top-[4px] group-hover:w-[184px] z-10 duration-500"
  >
    <svg
      width="25px"
      height="25px"
      viewBox="0 0 1024 1024"
      xmlns="http://www.w3.org/2000/svg"
    >
      <path
        fill="#000000"
        d="M224 480h640a32 32 0 1 1 0 64H224a32 32 0 0 1 0-64z"
      ></path>
      <path
        fill="#000000"
        d="m237.248 512 265.408 265.344a32 32 0 0 1-45.312 45.312l-288-288a32 32 0 0 1 0-45.312l288-288a32 32 0 1 1 45.312 45.312L237.248 512z"
      ></path>
    </svg>
  </div>
  <p class="translate-x-2">Go Back</p>
</button>

            {/* back button end */}

            <div className="flex justify-end mb-4">
                <button
                    onClick={handleCallUser}
                    className="bg-green-500 text-white py-2 px-4 rounded mr-2"
                >
                    <PhoneIcon className="w-6 h-6" />
                </button>
                <button
                    onClick={handleCallUser}
                    className="bg-blue-500 text-white py-2 px-4 rounded"
                >
                    <VideoCameraIcon className="w-6 h-6" />
                </button>
            </div>

            <div className="flex flex-col flex-grow overflow-y-auto mb-4">
                <div className="flex-grow">
                    {messages.map((message) => (
                        <div key={message.id} className="bg-white p-4 mb-2 rounded shadow">
                            <p className="font-semibold">{message.sender}</p>
                            <p>{message.text}</p>
                            <button
                                onClick={() => handleDeleteMessage(message.id)}
                                className="text-red-500"
                            >
                                Delete
                            </button>
                        </div>
                    ))}
                    <div ref={messageEndRef} />
                </div>
            </div>

            <div className="flex">
                <input
                    type="text"
                    value={messageText}
                    onChange={(e) => setMessageText(e.target.value)}
                    placeholder="Type your message..."
                    className="w-full px-3 py-2 border rounded"
                />
                <button
                    onClick={handleSendMessage}
                    className="bg-blue-500 text-white py-2 px-4 rounded ml-2"
                >
                    Send
                </button>
            </div>
        </div>
    );
};

export default ChatPage;
